#include<iostream>using namespace std;int main(){    int test;    cin >> test;    for(int i =1;i<=test;i++){        string str;        cin >> str;        int  a=0,b=0,c=0;        int length = str.length();        for(int j =0;j<length;j++){            if(str[j]=='A'){                a++;            }            else if(str[j]=='B'){                b++;            }            else{                c++;            }        }        if(a+c==b){            cout << "YES"<<endl;        }        else{            cout << "NO"<<endl;        }    }}