//// Created by fazalqadir on 2/5/26.//#include <iostream>using namespace std;int main(){    int test;    cin >> test;    for (int i =1;i<=test;i++)    {        int len ;        cin >> len;        string path;        cin >> path;        int coins =0,hurdles=0;        for (int j =0;j<len;j++)        {            if (path[j]=='@')            {                coins++;                hurdles=0;            }            else if (path[j]=='*')            {                hurdles++;                if (hurdles==2)                {                    break;;                }            }            else            {             hurdles=0;               }        }        cout << coins<<endl;    }}