//// Created by fazalqadir on 1/30/26.//#include <iostream>using namespace std;int main(){    int test;    cin >> test;    for (int i =1;i<=test;i++)    {        int a,x,y;        cin >> a>>x>>y;        int res1 = x-a;        int res2 = y-a;        int distance = x-y;        if (distance<0)        {            distance*=-1;        }        if (res1<0)        {            res1*=-1;        }        if (res2<0)        {            res2*=-1;        }        if (res1>distance||res2>distance)        {            cout << "YES"<<endl;        }        else        {            cout << "NO"<<endl;        }    }}