//// Created by fazalqadir on 1/19/26.//#include<iostream>using namespace std;int main(){    int n;    cin >> n;    int arr[n];    int police_man =0;    int crime=0;    for(int i=0;i<n;i++)    {        cin >> arr[i];        if (arr[i]>10)        {            arr[i]=10;        }        if (arr[i]<0&&police_man==0)        {            crime++;        }        else if (arr[i]>=0)        {            police_man+=arr[i];        }        else if (arr[i]<0)        {            police_man--;        }    }    cout << crime;}